class FileSource extends RichSourceFunction[String]() with StoppableFunction{
    var startSystemTime = System.nanoTime();
    var startDataTime = 10753295594424116L;
    var stoped = false;

    def shiftIterator(fileIterator : Iterator[String] ): Unit = {

        var line = fileIterator.next()
        var lineDataTimestamp = line.split(",")(1).toLong;

        while(lineDataTimestamp<=startDataTime){
            line = fileIterator.next()
            lineDataTimestamp = line.split(",")(1).toLong;
        }
    }

    def run(sc: SourceContext[String]): Unit = {
        var fileIterator = scala.tools.nsc.io.File("debs2013.data").lines();

        shiftIterator(fileIterator) 
        startSystemTime = System.nanoTime();

        for(line <-scala.tools.nsc.io.File("debs2013.data").lines()){

            if(Thread.interrupted() || stoped){
                return
            }
            var dataTimestamp = line.split(",")(1).toLong;
            if(dataTimestamp > startDataTime){
                var elapsedSystemTime = System.nanoTime() - startSystemTime;
                var elapsedDataTime = (dataTimestamp - startDataTime) / 1000; // picoseconds to nanoseconds
                while(elapsedDataTime > elapsedSystemTime && !Thread.interrupted()){
                    elapsedSystemTime = System.nanoTime() - startSystemTime;
                    // active waiting for time
                } 
                sc.collect(line);
            }
        }
    }

    def stop() : Unit= {
        stoped = true 
    }

    def cancel() : Unit= {
        stoped = true 
    }

}

var fs = new FileSource()


/**
 * sid, ts, 
 * x, y, z, 
 * |v| in μm/s,
 * |a| is zero when the ball is at a fixed position and not 9.81 m/s²).
 * vx, vy, vz, describe direction by a vector with size of 10,000
 * ax, ay, az
 * 
 * 
 * Hence, the speed of the object in x-direction in SI-units (m/s) is calculated by 
 *          v'x = |v| * vx * 1e-4 * 1e-6
 * 
 */

case class DebsDataItem(sid:Int, ts:Long, x:Int, y:Int, z:Int, v:Int, a:Int , vx:Int, vy:Int, vz:Int, ax:Int, ay:Int, az:Int )

var debs2013DataStream = senv.addSource(fs)

var debsItemDataStream = debs2013DataStream.map(line=>{
    val items = line.split(",")
    DebsDataItem(
        items(0).toInt,
        items(1).toLong,
        items(2).toInt,
        items(3).toInt,
        items(4).toInt,
        items(5).toInt,
        items(6).toInt,
        items(7).toInt,
        items(8).toInt,
        items(9).toInt,
        items(10).toInt,
        items(11).toInt,
        items(12).toInt
        )
})

